import 'package:uniccon/component/secondary_text.dart';
import 'package:uniccon/export.dart';
import 'package:uniccon/utils/utils.dart';

class InputFieldWidget extends StatelessWidget {
  Icon? icon;
  final TextInputType textInputType;
  final String labelText;
  final TextEditingController textController;
  final bool autoFocus;
  final String? Function(String?)? validate;
  bool isPassword;
  String? hintText;
  final TextInputAction textInputAction;
  //TODO BorderError, Validation

  InputFieldWidget(
      {Key? key,
      this.icon,
      this.isPassword = false,
      required this.textInputType,
      required this.labelText,
      required this.textController,
      required this.autoFocus,
      this.validate,
      this.hintText,
      required this.textInputAction})
      : super(key: key);

  @override
  Widget build(BuildContext context) {
    return SizedBox(
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          SecondaryText(
            text: labelText,
          ),
          addVerticalSpace(12),
          SizedBox(
            height: 52,
            child: TextFormField(
              controller: textController,
              validator: validate,
              textInputAction: textInputAction,
              keyboardType: textInputType,
              obscureText: isPassword == true ? true : false,
              decoration: InputDecoration(
                focusedBorder: OutlineInputBorder(
                    borderSide:
                        const BorderSide(color: AppColors.primary, width: 0.9),
                    borderRadius: BorderRadius.circular(12)),
                enabledBorder: OutlineInputBorder(
                    borderSide:
                        const BorderSide(color: AppColors.border, width: 0.9),
                    borderRadius: BorderRadius.circular(12)),
                filled: true,
                hintText: hintText,
                // hintStyle: GoogleFonts.nunitoSans(
                //   fontWeight: FontWeight.w400,
                //   fontSize: 16.0,
                //   height: 1,
                //   color: AppColors.secondaryText,
                // ),
                fillColor: Colors.white.withOpacity(0.0),
                suffixIcon: isPassword
                    ? const Padding(
                        padding: EdgeInsets.only(
                            top: 10, left: 0, right: 17, bottom: 10),
                        child: Icon(Icons.remove_red_eye))
                    : const Icon(Icons.key),
              ),
            ),
          ),
        ],
      ),
    );
  }
}
