import 'package:flutter/material.dart';

class Dashboard extends StatefulWidget {
  const Dashboard({Key? key}) : super(key: key);

  @override
  _DashboardState createState() => _DashboardState();
}

class _DashboardState extends State<Dashboard> {
  List pages = const [
    HomePage(),
    Bible(),
    Favorites(),
    SettingsPage(),
  ];

  int currentIndex = 0;

  void onTap(int index) {
    setState(() {
      currentIndex = index;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: pages[currentIndex],
      bottomNavigationBar: BottomNavigationBar(
        type: BottomNavigationBarType.fixed,
        backgroundColor: Colors.white,
        selectedFontSize: 14.0,
        unselectedFontSize: 14,
        onTap: onTap,
        currentIndex: currentIndex,
        selectedItemColor: Colors.indigo,
        unselectedItemColor: Colors.indigoAccent.withOpacity(0.7),
        selectedLabelStyle: const TextStyle(fontWeight: FontWeight.w300),
        showUnselectedLabels: true,
        showSelectedLabels: true,
        elevation: 4,
        items: const [
          BottomNavigationBarItem(
              label: "Home",
              icon: Padding(
                padding: EdgeInsets.only(top: 14.0, bottom: 7),
                child: SizedBox(
                    width: 18, height: 18, child: Icon(Icons.home_filled)),
              )),
          BottomNavigationBarItem(
              label: "Bible",
              icon: Padding(
                padding: EdgeInsets.only(top: 14.0, bottom: 7),
                child: SizedBox(
                    width: 18,
                    height: 18,
                    child: Icon(Icons.menu_book_rounded)),
              )),
          BottomNavigationBarItem(
              label: "Favorites",
              icon: Padding(
                padding: EdgeInsets.only(top: 14.0, bottom: 7),
                child: SizedBox(
                    width: 18, height: 18, child: Icon(Icons.favorite)),
              )),
          BottomNavigationBarItem(
              label: "Settings",
              icon: Padding(
                padding: EdgeInsets.only(top: 14.0, bottom: 7),
                child: SizedBox(
                    width: 18, height: 18, child: Icon(Icons.settings)),
              )),
        ],
      ),
    );
  }
}
